// src/components/RecipeEdit.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

const RecipeEdit = () => {
    const { id } = useParams();
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const [instructions, setInstructions] = useState('');

    useEffect(() => {
        fetch(`http://localhost:8080/recepti/${id}`)
            .then(response => response.json())
            .then(data => {
                setTitle(data.title);
                setDescription(data.description);
                setInstructions(data.instructions);
            })
            .catch(error => console.error('Error fetching recipe:', error));
    }, [id]);

    const handleSubmit = (e) => {
        e.preventDefault();

        const updatedRecipe = { title, description, instructions };

        fetch(`http://localhost:8080/recepti/${id}`, {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(updatedRecipe),
        })
        .then(() => {
            window.location.href = '/';
        })
        .catch(error => console.error('Error updating recipe:', error));
    };

    return (
        <div>
            <h1>Edit Recipe</h1>
            <form onSubmit={handleSubmit}>
                <label>Title:</label>
                <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} required />
                
                <label>Description:</label>
                <textarea value={description} onChange={(e) => setDescription(e.target.value)} required></textarea>
                
                <label>Instructions:</label>
                <textarea value={instructions} onChange={(e) => setInstructions(e.target.value)} required></textarea>

                <button type="submit">Save Changes</button>
            </form>
        </div>
    );
};

export default RecipeEdit;
