// src/components/RecipeList.js
import React, { useEffect, useState } from 'react';

const RecipeList = () => {
    const [recipes, setRecipes] = useState([]);

    useEffect(() => {
        fetch('http://localhost:8080/recepti')
            .then(response => response.json())
            .then(data => setRecipes(data))
            .catch(error => console.error('Error fetching recipes:', error));
    }, []);

    const deleteRecipe = (id) => {
        fetch(`http://localhost:8080/recepti/${id}`, {
            method: 'DELETE',
        })
        .then(() => {
            setRecipes(recipes.filter(recipe => recipe.id !== id));
        })
        .catch(error => console.error('Error deleting recipe:', error));
    };

    return (
        <div className="container mt-5">
            <h1 className="mb-4">Recipe List</h1>
            <button 
                className="btn btn-success mb-3" 
                onClick={() => window.location.href = '/new-recipe'}>
                Add New Recipe
            </button>
            <div className="list-group">
                {recipes.map(recipe => (
                    <div key={recipe.id} className="list-group-item mb-3">
                        <h2 className="h5">{recipe.title}</h2>
                        <p>{recipe.description}</p>
                        <button 
                            className="btn btn-warning me-2" 
                            onClick={() => window.location.href = `/edit-recipe/${recipe.id}`}>
                            Edit
                        </button>
                        <button 
                            className="btn btn-danger" 
                            onClick={() => deleteRecipe(recipe.id)}>
                            Delete
                        </button>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default RecipeList;
