// src/components/RecipeList.js
import React, { useEffect, useState } from 'react';

const RecipeList = () => {
    const [recipes, setRecipes] = useState([]);

    useEffect(() => {
        fetch('http://localhost:8080/recepti')
            .then(response => response.json())
            .then(data => setRecipes(data))
            .catch(error => console.error('Error fetching recipes:', error));
    }, []);

    const deleteRecipe = (id) => {
        fetch(`http://localhost:8080/recepti/${id}`, {
            method: 'DELETE',
        })
        .then(() => {
            setRecipes(recipes.filter(recipe => recipe.id !== id));
        })
        .catch(error => console.error('Error deleting recipe:', error));
    };

    return (
        <div>
            <h1>Recipe List</h1>
            <button onClick={() => window.location.href = '/new-recipe'}>Add New Recipe</button>
            <ul>
                {recipes.map(recipe => (
                    <li key={recipe.id}>
                        <h2>{recipe.title}</h2>
                        <p>{recipe.description}</p>
                        <button onClick={() => window.location.href = `/edit-recipe/${recipe.id}`}>Edit</button>
                        <button onClick={() => deleteRecipe(recipe.id)}>Delete</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default RecipeList;
